mapper.insert:
INSERT INTO TB_USER(
	USER_ZIP
	, USER_TEL
	, USER_RN
	, USER_PASS
	, USER_NM
	, USER_MEMO
	, USER_LEV
	, USER_ID
	, USER_HP
	, USER_FAX
	, USER_EMAIL
	, USER_DTL_ADR
	, USER_BANK_NM
	, USER_ADR
	, USER_ACCOUNT_NO
	, UPT_ID
	, UPT_DT
	, SIGUNGU_NM
	, SIDO_NM
	, INS_ID
	, INS_DT
	, HOSP_NO
	, FARM_NO
) VALUES(
	#{userZip}
	, #{userTel}
	, #{userRn}
	, #{userPass}
	, #{userNm}
	, #{userMemo}
	, #{userLev}
	, #{userId}
	, #{userHp}
	, #{userFax}
	, #{userEmail}
	, #{userDtlAdr}
	, #{userBankNm}
	, #{userAdr}
	, #{userAccountNo}
	, #{userId}
	, now()
	, #{sigunguNm}
	, #{sidoNm}
	, #{userId}
	, now()
	, #{hospNo}
	, #{farmNo}
)

mapper.update:
	UPDATE TB_USER
	   SET USER_ZIP
		   , USER_TEL	= #{userTel}
		   , USER_RN	= #{userRn}
		   , USER_PASS	= #{userPass}
		   , USER_NM	= #{userNm}
		   , USER_MEMO	= #{userMemo}
		   , USER_LEV	= #{userLev}
		   , USER_ID	= #{userId}
		   , USER_HP	= #{userHp}
		   , USER_FAX	= #{userFax}
		   , USER_EMAIL	= #{userEmail}
		   , USER_DTL_ADR	= #{userDtlAdr}
		   , USER_BANK_NM	= #{userBankNm}
		   , USER_ADR		= #{userAdr}
		   , USER_ACCOUNT_NO= #{userAccountNo}
		   , UPT_ID		= #{uptId}
		   , UPT_DT		= now()
		   , SIGUNGU_NM	= #{sigunguNm}
		   , SIDO_NM	= #{sidoNm}
		   , HOSP_NO	= #{hospNo}
		   , FARM_NO	= #{farmNo}
     WHERE USER_NO = #{userNo}

UPDATE TB_HOSPITAL
   SET HOSP_NM				= #{hospNm}
	   ,HOSP_TEL			= #{hospTel}
	   ,HOSP_HP				= #{hospHp}
	   ,HOSP_EMAIL			= #{hospEmail}
	   ,HOSP_FAX			= #{hospFax}
	   ,HOSP_ADR			= #{hospAdr}
	   ,HOSP_DTL_ADR		= #{hospDtlAdr}
	   ,HOSP_ZIP			= #{hospZip}
	   ,HOSP_CEO			= #{hospCeo}
	   ,HOSP_RN				= #{hospRn}
	   ,HOSP_SIDO_NM		= #{hospSidoNm}
	   ,HOSP_SIGUNGU_NM		= #{hospSigunguNm}
	   ,HOSP_ACCOUNT_NO		= #{hospAccountNo}
	   ,HOSP_BANK_NM		= #{hospBankNm}
	   ,HOSP_MEMO			= #{hospMemo}
	   ,PAY_GB				= #{payGb}
	   ,PAY_DATE			= #{payDate}
	   ,PAY_MANAGER_NM		= #{payManagerNm}
	   ,PAY_MANAGER_HP		= #{payManagerHp}
	   ,PAY_MEMO			= #{payMemo}
	   ,HOSP_FIRST_DOCTOR	= #{hospFirstDoctor}
	   
	   		,HOSP_FIRST_DOCTOR_NM	= (SELECT USER_NM FROM TB_USER WHERE USER_NO = #{hospFirstDoctor})
	   
	   
	   		,HOSP_FIRST_DOCTOR_NM	= #{hospFirstDoctorNm}
	   
	   ,UPT_DT				= NOW()
	   ,UPT_ID				= #{insId}
WHERE HOSP_NO = #{hospNo}

mapper.delete:
DELETE FROM TB_HOSPITAL
WHERE HOSP_NO IN (
	
		${item}
	
)

DELETE FROM TB_FARM
WHERE FARM_NO IN (
	
		${item}
	
)

