mapper.insert:
INSERT INTO TB_USER(
	USER_ZIP
	, USER_TEL
	, USER_RN
	, USER_PASS
	, USER_NM
	, USER_MEMO
	, USER_LEV
	, USER_ID
	, USER_HP
	, USER_FAX
	, USER_EMAIL
	, USER_DTL_ADR
	, USER_BANK_NM
	, USER_ADR
	, USER_ACCOUNT_NO
	, UPT_ID
	, UPT_DT
	, SIGUNGU_NM
	, SIDO_NM
	, INS_ID
	, INS_DT
	, HOSP_NO
	, FARM_NO
) VALUES(
	#{userZip}
	, #{userTel}
	, #{userRn}
	, #{userPass}
	, #{userNm}
	, #{userMemo}
	, #{userLev}
	, #{userId}
	, replace(replace(#{userHp}, '-',''), '-', '')
	, replace(replace(#{userFax}, '-',''), '-', '')
	, #{userEmail}
	, #{userDtlAdr}
	, #{userBankNm}
	, #{userAdr}
	, #{userAccountNo}
	, #{userId}
	, now()
	, #{sigunguNm}
	, #{sidoNm}
	, #{userId}
	, now()
	, #{hospNo}
	, #{farmNo}
)

mapper.update:
	UPDATE TB_USER
