	   	   		, HOSP_NO	= #{hospNo}
	   	   		, FARM_NO	= ''
	   	   
	   	   
	   	   		, FARM_NO	= #{farmNo}
	   	   		, HOSP_NO	= ''
	   	   
	   	   
	   	   		, USER_STAT	= #{userStat}
	   	   
	   	   
	   	   		, USER_WORK_GB	= #{userWorkGb}
	   	   
	   	   
	   	   		, ADMIN_LEV	= #{adminLev}
	   	   
     WHERE USER_NO = #{userNo}

mapper.select:
SELECT A.* , (SELECT HOSP_NM FROM TB_HOSPITAL WHERE HOSP_NO = A.HOSP_NO) AS HOSP_NM , (SELECT FARM_NM FROM TB_FARM WHERE FARM_NO = A.FARM_NO) AS FARM_NM , (SELECT CODE_NM FROM TB_CODE WHERE CODE_ID = A.USER_WORK_GB) AS USER_WORK_GB_NM FROM TB_USER A WHERE 1=1
AND USER_NO = #{userNo}
AND USER_NM LIKE CONCAT('%', #{userNm}, '%')
AND USER_WORK_GB = #{userWorkGb}
AND ADMIN_YN = #{adminYn}
AND USER_STAT = #{userStat}
ORDER BY USER_NO

mapper.update:
UPDATE TB_HOSPITAL
   SET HOSP_NM				= #{hospNm}
	   ,HOSP_TEL			= #{hospTel}
	   ,HOSP_HP				= #{hospHp}
	   ,HOSP_EMAIL			= #{hospEmail}
	   ,HOSP_FAX			= #{hospFax}
	   ,HOSP_ADR			= #{hospAdr}
	   ,HOSP_DTL_ADR		= #{hospDtlAdr}
	   ,HOSP_ZIP			= #{hospZip}
	   ,HOSP_CEO			= #{hospCeo}
	   ,HOSP_RN				= #{hospRn}
	   ,HOSP_SIDO_NM		= #{hospSidoNm}
	   ,HOSP_SIGUNGU_NM		= #{hospSigunguNm}
	   ,HOSP_ACCOUNT_NO		= #{hospAccountNo}
	   ,HOSP_BANK_NM		= #{hospBankNm}
	   ,HOSP_MEMO			= #{hospMemo}
	   ,PAY_GB				= #{payGb}
	   ,PAY_DATE			= #{payDate}
	   ,PAY_MANAGER_NM		= #{payManagerNm}
	   ,PAY_MANAGER_HP		= #{payManagerHp}
	   ,PAY_MEMO			= #{payMemo}
	   ,HOSP_FIRST_DOCTOR	= #{hospFirstDoctor}
	   
	   		,HOSP_FIRST_DOCTOR_NM	= (SELECT USER_NM FROM TB_USER WHERE USER_NO = #{hospFirstDoctor})
	   
	   
	   		,HOSP_FIRST_DOCTOR_NM	= #{hospFirstDoctorNm}
	   
	   ,UPT_DT				= NOW()
	   ,UPT_ID				= #{insId}
WHERE HOSP_NO = #{hospNo}

mapper.delete:
DELETE FROM TB_HOSPITAL
WHERE HOSP_NO IN (
	
		${item}
	
)

