	, RQST_NO
	, HOSP_NO
	, USER_NO
	, ANIM_NO
	, FARM_NO
	, BOARD_NO
	, INSP_NO
	, TITLE
	, CONTENT
	, CLOSE_YN
) VALUES(
	#{fileGb}
	, #{filePath}
	, #{fileOriNm}
	, #{fileNewNm}
	, #{fileMemo}
	, #{rqstNo}
	, #{hospNo}
	, #{userNo}
	, #{animNo}
	, #{farmNo}
	, #{boardNo}
	, #{inspNo}
	, #{title}
	, #{content}
	, #{closeYn}
)

mapper.delete:
DELETE
  FROM TB_FILE
 WHERE 1=1
   AND FILE_GB = 'F001-01'

	AND HOSP_NO = #{hospNo}


	AND FARM_NO = #{farmNo}

DELETE
  FROM TB_FILE
 WHERE 1=1

   AND FILE_GB = #{fileGb}


	AND HOSP_NO = #{hospNo}


	AND FARM_NO = #{farmNo}


	AND RQST_NO = #{rqstNo}


	AND INSP_NO = #{inspNo}


	AND BOARD_NO = #{boardNo}


	AND FILE_NO = #{fileNo}

mapper.select:
SELECT MENU_NO , MENU_NM , UPP_MENU_NO , (SELECT MENU_NM FROM TB_MENU WHERE MENU_NO = A.UPP_MENU_NO) as UPP_MENU_NM , MENU_ORDER , MENU_DTL_MEMO , MENU_URL , CASE WHEN LEV_ONE = 'Y' THEN true ELSE false END AS LEV_ONE_CHK , CASE WHEN LEV_TWO = 'Y' THEN true ELSE false END AS LEV_TWO_CHK , CASE WHEN LEV_THREE = 'Y' THEN true ELSE false END AS LEV_THREE_CHK , CASE WHEN LEV_FOUR = 'Y' THEN true ELSE false END AS LEV_FOUR_CHK FROM TB_MENU A WHERE 1=1
AND LEV_ONE = #{levOne}
AND LEV_TWO = #{levTwo}
AND LEV_THREE = #{levThree}
AND LEV_FOUR = #{levFour}
ORDER BY MENU_NO
