mapper.select:
SELECT RQST_NO, HOSP_NO, HOSP_NM, USER_NO , USER_NM, ANIM_NO, ANIM_NM, FARM_NO , FARM_NM, DOC_NO, DOC_NM, ANIM_BUTLER , BUTLER_SIDO, BUTLER_SIGUNGU, BUTLER_SIGUNGU_CD , ANIM_BIRTH, ANIM_FIRST_CD, ANIM_SECOND_CD, ANIM_THIRD_CD , ANIM_SEX, RQST_DT, PROC_STAT , (SELECT CODE_NM FROM TB_CODE B WHERE B.CODE_ID = A.PROC_STAT) AS PROC_STAT_NM , RQST_MEMO, PAY_GB, PRICE, PAY_STAT , PAYED_DT, RESULT, RESULT_WRITER, FINISH_DT , DIAG_UP_CD, DIAG_CD , (SELECT USER_NM FROM TB_USER B WHERE B.USER_ID = A.RESULT_WRITER) AS RESULT_WRITER_NM , (SELECT CODE_NM FROM TB_CODE B WHERE B.CODE_ID = A.DIAG_CD) AS DIAG_CD_NM , CONVERT(FINAL_MEMO USING UTF8) AS FINAL_MEMO , (SELECT CODE_NM FROM TB_CODE WHERE CODE_ID = (SELECT INSP_SECOND_CD FROM TB_INSPECTION B WHERE A.RQST_NO = B.RQST_NO LIMIT 1)) AS INSP_TYPE , PDL_NO FROM TB_RCEPT A WHERE PROC_STAT IN
#{arr}
ORDER BY INS_DT DESC

SELECT A.* FROM TB_HOSPITAL A WHERE 1=1
AND HOSP_NO = #{hospNo}
ORDER BY HOSP_NO

SELECT *
  FROM TB_FILE
 WHERE 1=1
   AND RQST_NO = #{rqstNo}
   AND INSP_NO = #{inspNo}

SELECT *
  FROM TB_MEDI_HISTORY
 WHERE RQST_NO = #{rqstNo}

SELECT RQST_NO, HOSP_NO, HOSP_NM, USER_NO , USER_NM, ANIM_NO, ANIM_NM, FARM_NO , FARM_NM, DOC_NO, DOC_NM, ANIM_BUTLER , BUTLER_SIDO, BUTLER_SIGUNGU, BUTLER_SIGUNGU_CD , ANIM_BIRTH, ANIM_FIRST_CD, ANIM_SECOND_CD, ANIM_THIRD_CD , ANIM_SEX, RQST_DT, PROC_STAT , (SELECT CODE_NM FROM TB_CODE B WHERE B.CODE_ID = A.PROC_STAT) AS PROC_STAT_NM , RQST_MEMO, PAY_GB, PRICE, PAY_STAT , PAYED_DT, RESULT, RESULT_WRITER, FINISH_DT , DIAG_UP_CD, DIAG_CD , (SELECT USER_NM FROM TB_USER B WHERE B.USER_ID = A.RESULT_WRITER) AS RESULT_WRITER_NM , (SELECT CODE_NM FROM TB_CODE B WHERE B.CODE_ID = A.DIAG_CD) AS DIAG_CD_NM , CONVERT(FINAL_MEMO USING UTF8) AS FINAL_MEMO , (SELECT CODE_NM FROM TB_CODE B WHERE B.CODE_ID = A.ANIM_FIRST_CD) AS ANIM_FIRST_NM , (SELECT CODE_NM FROM TB_CODE B WHERE B.CODE_ID = A.ANIM_SECOND_CD) AS ANIM_SECOND_NM , (SELECT CODE_NM FROM TB_CODE B WHERE B.CODE_ID = A.ANIM_THIRD_CD) AS ANIM_THIRD_NM , CASE WHEN ANIM_SEX = 'M' THEN '수컷' WHEN ANIM_SEX = 'F' THEN '암컷' ELSE '모름' END ANIM_SEX_NM , PDL_NO , INSPECTION FROM TB_RCEPT A WHERE 1=1
AND RQST_NO = #{rqstNo}
AND PDL_NO = #{pdlNo}

SELECT *
  FROM TB_USER
 WHERE USER_NO = #{userNo}

