mapper.insert:
INSERT
  INTO TB_ANTIBIOTIC (
       INSP_NO     , ANTI_NO     , ANTI_NAME
     , CAPACITY    , NICK_NAME
     , MINI        , SCOPE       , MAXI
     , RES1        , RES2        , ANTI_MEMO
     , INS_DT      , INS_ID )
VALUES (
       #{inspNo}   , #{antiNo}   , #{antiName}
     , #{capacity} , #{nickName}
     , #{mini}     , #{scope}    , #{maxi}
     , #{res1}     , #{res2}     , #{antiMemo}
     , NOW()       , #{insId} )
    ON DUPLICATE KEY
UPDATE ANTI_NAME = #{antiName}
     , CAPACITY = #{capacity}
     , NICK_NAME = #{nickName}
     , MINI = #{mini}
     , SCOPE = #{scope}
     , MAXI = #{maxi}
     , RES1 = #{res1}
     , RES2 = #{res2}
     , ANTI_MEMO = #{antiMemo}
     , UPT_DT = NOW()
     , UPT_ID = #{insId}

INSERT
  INTO TB_PCR (
       INSP_NO  , SMPL_NAME
     , POSITIVE , NEGATIVE
     , RESULT   , MEMO
     , INS_DT   , INS_ID )
VALUES (
       #{inspNo}   , #{smplName}
     , #{positive} , #{negative}
     , #{result}   , #{memo}
     , NOW()       , #{insId} )
    ON DUPLICATE KEY
UPDATE RESULT = #{result}
     , POSITIVE = #{positive}
     , NEGATIVE = #{negative}
     , MEMO = #{memo}
     , UPT_DT = NOW()
     , UPT_ID = #{insId}
