mapper.select:
SELECT (SELECT COUNT(*) AS CNT FROM TB_RCEPT WHERE DATE_FORMAT(INS_DT,'%Y%m') = #{searchStr} AND PROC_STAT = 1) AS REQ_CNT , (SELECT COUNT(*) AS CNT FROM TB_RCEPT WHERE DATE_FORMAT(INS_DT,'%Y%m') = #{searchStr} AND PROC_STAT = 2) AS REC_CNT , (SELECT COUNT(*) AS CNT FROM TB_RCEPT WHERE DATE_FORMAT(INS_DT,'%Y%m') = #{searchStr} AND PROC_STAT = 3) AS PROC_CNT , (SELECT COUNT(*) AS CNT FROM TB_RCEPT WHERE DATE_FORMAT(INS_DT,'%Y%m') = #{searchStr} AND PROC_STAT IN (4,5)) AS FIN_CNT FROM TB_RCEPT WHERE 1=1
AND INS_ID = #{insId}
AND DATE_FORMAT(INS_DT,'%Y%m') = #{searchStr} GROUP BY DATE_FORMAT(INS_DT,'%Y%m')
